import axios from 'axios';
import {parser} from '../utils/parser';

export function load_language(file:string) 
{
    var data = parser(file);
    if (data) {
        global.lang_files = data;
        return
    }

    axios.get(file).then(function (response) {
        global.lang_files = response.data;
        return
    });

}