import  * as express from 'express';
import  * as cookieParser from 'cookie-parser';
import {languageParser,load_language} from '../src';

load_language('https://cdn.jsdelivr.net/gh/lim10tech/lang/output.json');

var app = express();
app.use(cookieParser());
app.use('/',
    languageParser('languages'),
    (req,res:any,next) => {
    res.send(res.lang);
});

app.listen(80);